cess");}if (@$_REQUEST["operacao"] == "excluir") {$fachada = new Fachada();$idcon_livro_usuario 		= addslashes(@$_REQUEST["nId"]);$Objcon_livro_usuario = $fachada->getCon_livro_usuario($idcon_livro_usuario);$excluir = $fachada->desativarCon_livro_usuario($Objcon_livro_usuario); header("Location: index.php?op=sucess");} ?>